LayoutData.Style:
#targetMetadata,
#contextItems
{
	height: 120px;
}

#content
{
	padding: 30px 80px;
}



#targetMetadata input,
#contextItems input
{
	height: 100%;
	font-family: monospace;
	font-size: 14px;
	vertical-align: top;
	padding-top: 5px;
}

label
{
	min-width: 100px;
}

.button-selector
{
	flex-wrap:wrap;
	gap:0px;
}

.button-selector div
{
	width: 33%;
	gap: 0px;
	padding: 5px;
}

.button-selector label
{
	display: none;
}

.button-selector .toggle-button
{
	background: #222;
	color: #ccc;
	border-radius: 5px;
	font-family: monospace;
	font-size: 13px;
	margin-right: 5px;
}

.button-selector button:checked
{
	background: #aaa;
	color: #222;
	border-radius: 5px;
}

.button-selector button::before,
.button-selector button::after
{
	display: none;
}

