#title
{
	font-size: 1.5em;
}

.button-outline
{

	margin: 10px;
	padding: 10px;
	border-radius: 10px;
	border: 1px solid #666;
}

#projectType
{
	height: 120px;
	justify-content: flex-end;
	gap:0px;
}

#projectType .toggle-button
{
	height: 100%;
	width: 100vh;

	background: transparent;
}

#projectType .toggle-button:hover
{
	background: transparent;
}

#projectType button::before
{
	height: 100%;
	display: none;
	background: transparent;
}

#projectType label
{
	display: none;
}

