LayoutData.Style:
body
{
	font-size: 15px;	
}





tr:focus
{
	background: rgba(255,255,255, 0.07);
}

#footer,
#header
{
	display: none;
}



#content
{
	padding: 15px;
	align-items: flex-start;
	background: #333;
	box-shadow: inset 0px 0px 5px black;
	padding-top: 24px;

}

#content::before
{
	content: '';

	background: #222;
	height: 20px;
	border-bottom: 1px solid #555;
}

.header-label
{
	font-size: 1.1em;
	font-weight: bold;
	background: linear-gradient(to bottom, #222, #202020);
	color: #ddd;
	border-radius: 4px;
	padding: 0px;
}



.toggle-button
{
	background: transparent;
}

.toggle-button:hover
{
	background: transparent;
}

.tag-button
{
	font-size: 13px;
}

.category-button
{
	transform: none;
	padding: 0px;
	color: #bbb;
	width: auto;
	font-size: 14px !important;
	font-weight: 500;
	border-radius: 0px;
	text-align: center !important;
	background: #242424;
	height: 28px;
}

.category-button button:hover
{
	color: #eee;
	background: #222;
}

.category-button:active
{
	color: #fff;

	transform: scale(98%);

}

.category-button:checked
{
	background: #bbb;
	color: #222;
}

.first-child
{
	border-radius: 50% 0px 50% 0px;
}

.last-child
{
	border-radius: 0% 50% 0% 50%;
}

.category-button::before
{
	display:none;
}

.category-button::after
{
	display:none;
}

